package com.example;

import com.example.entity.*;
import com.example.model.*;
import com.example.repository.*;
import com.example.service.*;
import org.junit.Test;
import org.junit.runner.RunWith;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.boot.test.context.SpringBootTest;
import org.springframework.boot.test.mock.mockito.MockBean;
import org.springframework.test.context.junit4.SpringRunner;

import java.util.ArrayList;
import java.util.Arrays;
import java.util.List;

import static org.junit.Assert.assertEquals;
import static org.mockito.Mockito.*;

@RunWith(SpringRunner.class)
@SpringBootTest
public class ProductServiceTest
{
    @MockBean private ProductRepository productRepository;
    @MockBean private PromotedProductRepository promotedProductRepository;
    @MockBean private OpportunityRepository opportunityRepository;
    @MockBean private ImageRepository imageRepository;
    @MockBean private SubcategoryRepository subcategoryRepository;

    @Autowired private ProductService productService;
    @Autowired private PromotedProductService promotedProductService;
    @Autowired private OpportunityService opportunityService;
    @Autowired private ImageService imageService;

    @Test
    public void getProductByIdTest(){
        when(productRepository.findById(1L)).thenReturn(java.util.Optional.of(prepareProductEntity()));
        ProductModel result = productService.getProductById(1L);
        assertEquals(1, result.getId());
    }

    @Test
    public void getProductsBySubcategoryIdTest(){
        when(subcategoryRepository.findById(1L)).thenReturn(java.util.Optional.of(prepareSubcategoryEntity()));
        when(productRepository.findAllBySubcategory(any())).thenReturn(Arrays.asList(prepareProductEntity()));
        List<ProductModel> result = productService.getProductsBySubcategoryId(1L);
        assertEquals(1, result.size());
    }

    @Test(expected = UnsupportedOperationException.class)
    public void addProductTest(){
        when(subcategoryRepository.findById(1L)).thenReturn(java.util.Optional.of(prepareSubcategoryEntity()));
        productService.addProduct(1L, prepareProductModel());
        verify(productRepository).save(prepareProductEntity());
        atLeastOnce();
    }

    @Test
    public void editProductTest(){
        when(productRepository.findById(1L)).thenReturn(java.util.Optional.of(prepareProductEntity()));
        productService.editProduct(prepareProductModel());
        verify(productRepository).save(prepareProductEntity());
        atLeastOnce();
    }

    @Test
    public void removeProductTest(){
        productService.removeProduct(1L);
        verify(productRepository).deleteById(1L);
        atLeastOnce();
    }

    @Test
    public void saveImageTest(){
        imageService.saveImage(prepareImageModel());
        verify(imageRepository).save(prepareImageEntity());
        atLeastOnce();
    }

    @Test
    public void saveImagesTest(){
        imageService.saveImages(Arrays.asList(prepareImageModel()));
        verify(imageRepository).saveAll(any());
        atLeastOnce();
    }

    @Test
    public void getCategoryOfProductTest(){
        when(productRepository.findById(1L)).thenReturn(java.util.Optional.of(prepareProductEntity2()));
        CategoryModel result = productService.getCategoryOfProduct(1L);
        assertEquals(1, result.getId());
    }

    @Test
    public void getSubcategoryOfProductTest(){
        when(productRepository.findById(1L)).thenReturn(java.util.Optional.of(prepareProductEntity2()));
        SubcategoryModel result = productService.getSubcategoryOfProduct(1L);
        assertEquals(1, result.getId());
    }

    @Test
    public void getOpportunityTest(){
        Opportunity o = new Opportunity();
        o.setId(1L);
        o.setProduct(prepareProductEntity());
        o.setPromotionCode("TEST");
        o.setQuantity(1);

        when(opportunityRepository.findAll()).thenReturn(Arrays.asList(o));
        OpportunityModel result = opportunityService.getOpportunity();
        assertEquals(1, result.getId());
    }

    @Test
    public void setOpportunityTest(){
        OpportunityModel o = new OpportunityModel();
        o.setId(1L);
        o.setProduct(prepareProductModel());
        o.setPromotionCode("TEST");
        o.setQuantity(1);

        opportunityService.setOpportunity(o);
        verify(opportunityRepository).save(any());
        atLeastOnce();
    }

    @Test
    public void unsetOpportunityTest(){
        opportunityService.unsetOpportunity();
        verify(opportunityRepository).deleteAll();
        atLeastOnce();
    }

    @Test
    public void addPromotedProductTest(){
        when(productRepository.findById(1L)).thenReturn(java.util.Optional.of(prepareProductEntity2()));
        promotedProductService.addPromotedProduct(1L);
        verify(promotedProductRepository).save(any());
        atLeastOnce();
    }

    @Test
    public void removePromotedProductTest(){
        when(productRepository.findById(1L)).thenReturn(java.util.Optional.of(prepareProductEntity2()));
        promotedProductService.removePromotedProduct(1L);
        verify(promotedProductRepository).deleteById(1L);
        atLeastOnce();
    }

    @Test
    public void getAllPromotedProductsTest(){
        PromotedProduct p = new PromotedProduct();
        p.setId(1L);
        p.setProduct(prepareProductEntity());
        when(promotedProductRepository.findAll()).thenReturn(Arrays.asList(p));
        List<PromotedProductModel> result = promotedProductService.getAllPromotedProducts();
        assertEquals(1, result.size());
    }

    private Subcategory prepareSubcategoryEntity(){
        Category c = new Category();
        c.setId(1L);
        c.setName("CategoryTest");

        Subcategory s = new Subcategory();
        s.setId(1L);
        s.setName("TestSubcategory");
        s.setProducts(Arrays.asList(prepareProductEntity()));
        s.setCategory(c);
        return s;
    }

    private Subcategory prepareSubcategoryEntity2(){
        Category c = new Category();
        c.setId(1L);
        c.setName("CategoryTest");

        Subcategory s = new Subcategory();
        s.setId(1L);
        s.setName("TestSubcategory");
        s.setProducts(null);
        s.setCategory(c);
        return s;
    }

    private SubcategoryModel prepareSubcategoryModel(){
        CategoryModel c = new CategoryModel();
        c.setId(1L);
        c.setName("CategoryTest");

        SubcategoryModel s = new SubcategoryModel();
        s.setId(1L);
        s.setName("TestSubcategory");
        s.setProducts(null);
        s.setCategory(c);
        return s;
    }

    private Product prepareProductEntity()
    {
        Product c = new Product();
        c.setId(1L);
        c.setName("TestProduct");
        c.setPrice(1.0);
        c.setQuantity(1);
        c.setDescription("TestDescription");
        c.setImages(Arrays.asList(prepareImageEntity()));
        c.setSubcategory(null);
        return c;
    }

    private Product prepareProductEntity2()
    {
        Product c = new Product();
        c.setId(1L);
        c.setName("TestProduct");
        c.setPrice(1.0);
        c.setQuantity(1);
        c.setDescription("TestDescription");
        c.setImages(Arrays.asList(prepareImageEntity()));
        c.setSubcategory(prepareSubcategoryEntity2());
        return c;
    }

    private ProductModel prepareProductModel()
    {
        ProductModel c = new ProductModel();
        c.setId(1L);
        c.setName("TestProduct");
        c.setPrice(1.0);
        c.setQuantity(1);
        c.setDescription("TestDescription");
        c.setImages(Arrays.asList(prepareImageModel()));
        c.setSubcategory(prepareSubcategoryModel());
        return c;
    }

    private ImageModel prepareImageModel()
    {
        ImageModel i = new ImageModel();
        i.setId(1L);
        i.setImage("");
        return i;
    }

    private Image prepareImageEntity()
    {
        Image i = new Image();
        i.setId(1L);
        i.setImageBase64("");
        return i;
    }
}
